     *  https://ppt.yiyaozg.com/user/send-mail-active-link/
     *  post { "email": "xxx@gmail.com"}
     */
    public static function SendMailActiveLink($args, $post, $get)
    {
        $arr = Users::getOne("uid,email,eastatus,eastr", ['email' => $post['email']]);
        if (count($arr) > 0) {
            if ($arr['eastatus'] == 'Y') {
                return Str::msg(-1, "邮箱已经成功的验证过了, 帐号可以登陆了");
            } else if (strlen($arr['eastr']) == 32) {
                $send = CnsMail::Send($post['email'], '空灵触动科技 邮箱激活', Net::scheme() . "/user/valid-email/valid/" . $arr['eastr']);
