    /**
     *  绑定邮箱
     * @param $args
     * @param $post [ "email: 'xxx', 'authcode': 'xxxx']
     * @param $get
     */
    public static function BindEmail($args, $post, $get)
    {
        if ($post['authcode'] == $_SESSION['auth_bindemail_code']) {
            $arr = Users::getOne('email', ['uid' => $args['_tk_uid']]);
            $hasemail = false;
            if (count($arr) > 0) {
                if (strlen(trim($arr['email'])) > 5) {
                    $hasemail = true;
                }
            }
            if ($hasemail)
                Users::update(['email' => $post['email']], ['uid' => $args['_tk_uid']]);
            else
                Users::update(['email' => $post['email']], ['uid' => $args['_tk_uid']], "update users set email=:email, levelnum=levelnum+20  where uid=:uid");
            if (Users::$affectedRows == 1)
                return Str::msg(1, "邮箱绑定成功!");
            else
                return Str::msg(-1, "邮箱绑定失败!");
        } else {
            return Str::msg(-2, '验证码不匹配');
        }
    }

    /**
     * @param $args /act/checkmobile
     * @param $post ['mobile': ]
     *
     * @param $args /act/checkcode
     * @param $post ['mobile': , 'checkcode': ]
     *
     * @param $args /act/bind
     * @param $post ['mobile': , 'checkcode': , 'wx_user_openid': , 'bind_reg':'bind|reg']
     * @param $get
     */
    public static function WeixinBind($args, $post, $get)
    {
        //var_dump($_SESSION);
        //检查手机号是否已经存在
        if ($args['act'] == 'checkmobile') {
            $arr = Users::getOne('uid', ['mobile' => $post['mobile']]);
            if ($arr) {
                return Str::msg(1, "手机号已经注册");
            } else {
                return Str::msg(-1, "手机号未注册");
            }
        } //检查验证码
        else if ($args['act'] == 'checkcode') {
            if (!Str::validate([trim($post['mobile']), 'mobile'])) {
                return Str::msg(-1, '手机号码格式不正确!');
            }

            if (isset($_SESSION['auth_weixin_bind_checkcode_time']) && time() - $_SESSION['auth_weixin_bind_checkcode_time'] < 60)
                return Str::msg(-2, "验证码已发出,请稍后尝试");

            $_SESSION['auth_weixin_bind_checkcode'] = random_int(100000, 999999);
            $_SESSION['auth_weixin_bind_checkcode_time'] = time();

            if (CnsSMS::sendFulltext($post['mobile'], '您好! ' . $post['mobile'] . ', 您的验证码是: ' . $_SESSION['auth_weixin_bind_checkcode'] . ' 【空灵触动科技】')) {
                return Str::msg(1, "您的验证码已经发送!" . $_SESSION['auth_weixin_bind_checkcode']);
            } else {
                $_SESSION['auth_weixin_bind_checkcode_time'] = 0;
                return Str::msg(-3, "验证码发送失败,请检测手机号码是否有误,如有问题请联系在线客服");
            }
        } //绑定或注册手机
        else if ($args['act'] == 'bind') {
            if ($post['checkcode'] == $_SESSION['auth_weixin_bind_checkcode']) {
                if ($post['bind_reg'] == 'bind') {
                    Users::update(['wechat' => $post['wx_user_openid']], ['mobile' => $post['mobile']]);
                    if (Users::$affectedRows == 1) {
                        $arr = Users::getOne("uid,gid,email,mobile", ["mobile" => $post['mobile']]);
                        if (count($arr) > 0 && $arr['uid'] > 0) {
                            $token = CnsToken::token($arr['uid'], ["_tk_uid" => $arr['uid'], "_tk_gid" => $arr['gid'], '_tk_ip' => Net::clientip()], 30);
                            Users::update(['lastlogin' => date('Y-m-d H:i:s')], ['uid' => $arr['uid']]);
                            return Str::msg(1, '绑定且登陆成功', ["uid" => $arr['uid'], "mobile" => $arr['mobile'], "email" => $arr["email"]], $token);
                        } else
                            return Str::msg(-1, "绑定成功，但登陆失败!");
                    } else {
                        return Str::msg(-2, "绑定失败!");
                    }
                } else if ($post['bind_reg'] == 'reg') {
                    Users::insert(['mobile' => $post['mobile'], 'wechat' => $post['wx_user_openid'], 'nickname' => $post['nickname']]);
                    if (Users::$affectedRows == 1) {
                        $arr = Users::getOne("uid,gid,email,mobile", ["mobile" => $post['mobile']]);
                        if (count($arr) > 0 && $arr['uid'] > 0) {
                            $token = CnsToken::token($arr['uid'], ["_tk_uid" => $arr['uid'], "_tk_gid" => $arr['gid'], '_tk_ip' => Net::clientip()], 30);
                            Users::update(['lastlogin' => date('Y-m-d H:i:s')], ['uid' => $arr['uid']]);
                            return Str::msg(1, '注册且登陆成功', ["uid" => $arr['uid'], "mobile" => $arr['mobile'], "email" => $arr["email"]], $token);
                        } else
                            return Str::msg(-1, "注册成功，但登陆失败!");
                    } else {
                        return Str::msg(-2, "绑定失败!");
                    }
                }
            } else {
                return Str::msg(-3, "验证码不正确");
            }
        }
    }

