    /**
     * @param $args
     * @param $post
     * @param $get
     * @return string
     */
    public
    static function WeixinScan($args, $post, $get)
    {
        //已经生成二维码，则
        if (isset($post['wx_state'])) {
            $state = $post['wx_state'];
            $memstr = CnsMemcached::get($state);

            if ($memstr == '[]') {
                return Str::msg(-1, "用户还未扫码，请等待");
            } else if (!$memstr) {
                return Str::msg(-2, "还未生成二维码或已经过期");
            }

            //如果用户已经扫描，则删除cookie
            //删除cookie
            setcookie('wx_state', '', -1);

            $arr = json_decode($memstr, true);
            //如果用户已经绑定，则直接跳到用户中心
            if ($arr['hasbind'] == 1) {
                $token = CnsToken::token($arr['uid'], ["_tk_uid" => $arr['uid'], "_tk_gid" => $arr['gid'], '_tk_ip' => Net::clientip()], 30);
                Users::update(['lastlogin' => date('Y-m-d H:i:s')], ['uid' => $arr['uid']]);
                return Str::msg(1, '登陆成功', ["uid" => $arr['uid'], "gid" => $arr['gid'], "mobile" => $arr['mobile'], "email" => $arr["email"]], $token);
            } else {
                return Str::msg(1, "用户已经扫描但还未绑定或注册系统帐号", $arr);
            }
