     */
    public
    static function ProductSetSharepass($args, $post, $get)
    {
        if (!isset($post['pid']) || !isset($post['sharepass'])) {
            return Str::msg(-1, '作品ID或分享密码不能为空');
        }

        $hash = Str::PassMixed(password_hash($post['sharepass'], PASSWORD_DEFAULT));

        Products::update(['sharepass' => $hash], ['pid' => $post['pid'], 'uid' => $args['_tk_uid']]);
        if (Products::$affectedRows == 1) {
            return Str::msg(1, '分享密码设置成功');
        } else {
            return Str::msg(-2, '分享密码设置错误');
        }
    }

    /**
     * @param $args
     * @param $post [ 'pid'=>,  'sharepass'=> ]
     * @param $get
     */
    public
    static function ProductInfoShare($args, $post, $get)
    {
        if (!isset($args['pid']) || !isset($post['sharepass'])) {
            return Str::msg(-1, '作品ID或分享密码不能为空');
        }

        $pid = $args['pid'];
        $arr = Products::table('products')::getOne('*', ['pid' => $pid]);

        if ($arr) {
            if (password_verify($post['sharepass'], Str::PassMixed($arr['sharepass']))) {
                Products::table('products')::update([], ['pid' => $pid], 'update products set readnums=readnums+1 where pid=:pid');
                Readnums::table('readnums')::insert(['pid' => $pid, 'ruid' => $args['_tk_uid']]);
                return Str::msg(1, '分享密码正确', $arr);
            } else {
                return Str::msg(-2, '分享密码错误');
            }
        } else {
            return Str::msg(-3, '无此作品');
        }
    }

