    /**
     * @param $args
     * @param $post  array  ['pid'=>, 'type'=>video|audio|img|file', 'fname'=>'upfile', 'fext'=>'.ext' ]
     *                $_FILES['upfile']['name']  ['type']  ['size']  ['tmp_name']
     * @param $get
     */

    public
    static function ProductUpload($args, $post, $get)
    {
        //var_dump($post);
        //var_dump($_FILES);
        if (!Str::validate([$post['pid'], 'number']))
            return Str::msg(-1, "invalid pid");

        $path = "/uploads/products/" . $post['pid'];

        if (!file_exists(self::$rootdir . '/' . $path)) {
            if (!mkdir(self::$rootdir . '/' . $path))
                return Str::msg(-2, "create dir faild");
        }

        if (!isset($post['fext']))
            $post['fext'] = '';

        $arr = File::upload($_FILES[$post['fname']], self::$rootdir, $path, $post['type'], $post['fext'], isset($post['maxsize']) ? $post['maxsize'] * 1024 * 1024 : 150 * 1024 * 1024);
        if ($arr[0]) {
            return Str::msg(1, 'upload successfully', ['host' => Net::host(), 'url' => $arr[1]], $args['_tk_token']);
        } else {
            return Str::msg(-3, $arr[1]);
        }
    }

