    /**
     * @param $args
     * @param $post [act:<buy|sell>, orderid, pid, pname, subid, subname,status, pay_begin, pay_end]
     * @param $get
     * @return json
     */
    public static function ProductOrderList($args, $post, $get)
    {
        //列出此用户购买的所有订单
        if ($post['act'] == 'buy') {
            $where = " where uid = " . $args['_tk_uid'];
        } //列出此用户卖出的所有订单
        else if ($post['act'] == 'sell') {
            $arrPids = Products::getAll('pid', ['uid' => $args['_tk_uid']]);
            $arrPid = [];
            foreach ($arrPids as $k => $pid) {
                $arrPid[] = $pid['pid'];
            }
//i//var_dump($arrPid);
            if (count($arrPid) > 0)
                $where = " where pid in (" . implode(',', $arrPid) . ") ";
            else
                return Str::msg(-1, 'no records');
        } else {
            return Str::msg(-2, '错误操作类型');
        }

        $arrArgs = [];
        if (isset($post['orderid']) && trim($post['orderid']) != '') {
            $where .= " and orderid = :orderid ";
            $arrArgs['orderid'] = trim($post['orderid']);
        }

        if (isset($post['pid']) && trim($post['pid']) != '') {
            $where .= " and pid=:pid";
            $arrArgs['pid'] = trim($post['pid']);
        }

        if (isset($post['pname']) && trim($post['pname']) != '') {
            $where .= " and pname like '%:pname%'";
            $arrArgs['pname'] = trim($post['pname']);
        }

        if (isset($post['subid']) && trim($post['subid']) != '') {
            $where .= " and subid=:subid";
            $arrArgs['subid'] = trim($post['subid']);
        }

        if (isset($post['subname']) && trim($post['subname']) != '') {
            $where .= " and subname like '%:subname%' ";
            $arrArgs['subname'] = trim($post['subname']);
        }

        if (isset($post['status']) && trim($post['status']) != '') {
            $where .= " and status=:status ";
            $arrArgs['status'] = trim($post['status']);
        }

        if (isset($post['pay_begin']) && trim($post['pay_begin']) != '') {
            $where .= " and payed_time >= :pay_begin";
            $arrArgs['pay_begin'] = trim($post['pay_begin']);
        }

        if (isset($post['pay_end']) && trim($post['pay_end']) != '') {
            $pay_end = " and payed_time <= :pay_end";
            $arrArgs['pay_end'] = trim($post['pay_end']);
        }

        $sql = "select * from orders $where";
        $arrs = Orders::getAll('', $arrArgs, $sql);

        if ($arrs)
            return Str::msg(1, 'select successfully', $arrs, $args['_tk_token']);
        else
            return Str::msg(-3, 'no records');
    }


